    private void retActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_retActionPerformed
        // TODO add your handling code here:
        String bookID =bkid.getText();
        String studentID = sid.getText();

        try{
            Connection con = ConnectionProvider.getCon();
            Statement st = con.createStatement();
            st.executeUpdate("update issuebook set returnBook='Yes' where studentID="+studentID+" and bookID="+bookID);
            JOptionPane.showMessageDialog(null, "Book Return Successful");
            setVisible(false);
            new ReturnBook().setVisible(true);

        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null, "Connection Error");

        }
    }//GEN-LAST:event_retActionPerformed

