    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        // TODO add your handling code here:
        String bookID =bkid.getText();
        String studentID = sid.getText();

        try{
            Connection con = ConnectionProvider.getCon();
            Statement st = con.createStatement();
            ResultSet rs = st.executeQuery("select * from issuebook where bookID="+bookID+" and studentID ="+studentID);
            if(rs.next()){
                isd.setText(rs.getString(3));
                dd.setText(rs.getString(4));
                bkid.setEditable(false);
                sid.setEditable(false);

            }
            else{
                JOptionPane.showMessageDialog(null, "Invalid Book or Student ID!");
                setVisible(false);
                new ReturnBook().setVisible(true);
            }

        }
        catch(Exception e){
            e.printStackTrace();

        }
    }//GEN-LAST:event_searchActionPerformed

